import { InView } from "react-intersection-observer";
import { Swiper, SwiperSlide } from "swiper/react";
import { Mousewheel, Pagination } from "swiper";
import "swiper/css";
import "swiper/css/pagination";

import { useEffect, useState } from "react";
import Loader from "./Loader";

let url = "http://127.0.0.1:8000/api/shortsetV2/";
const ShortsV2 = () => {
  const [show, setShow] = useState(false);
  const [correctAnswer, setCorrectAnswer] = useState("");
  const [answerSelected, setAnswerSelected] = useState();
  const [currentVideo, setCurrentVideo] = useState();
  const [shorts, setShorts] = useState([]);
  const fetchAPi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setShorts(responseJSON);

    console.log(responseJSON);
  };
  useEffect(() => {
    fetchAPi();
  }, []);

  useEffect(() => {
    console.log(correctAnswer);
  }, [correctAnswer]);

  const handleText = () => {
    show ? setShow(false) : setShow(true);
  };
  const handleChangeRadio = (e) => {
    setAnswerSelected(e.target.value);
  };

  const getValue = () => {
    console.log(answerSelected);
  };

  const advance = () => {
    // const video = document.querySelector("video");
    currentVideo.currentTime = currentVideo.currentTime + 2;
  };
  function goBack() {
    ////   const video = document.querySelector("video");
    currentVideo.currentTime = currentVideo.currentTime - 2;
  }

  return (
    <>
      <Swiper
        mousewheel={true}
        style={{ height: "100vh" }}
        className="swiper-shorts"
        direction={"vertical"}
        // pagination={{ clickable: true }}
        slidesPerView={1}
        spaceBetween={30}
        modules={[Pagination, Mousewheel]}
      >
        {!shorts ? (
          <Loader></Loader>
        ) : (
          shorts.map((short) => {
            return (
              <SwiperSlide key={short.id}>
                <InView
                  os="div"
                  onChange={(inView, entry) => {
                    var video = entry.target.querySelector("video");

                    if (inView) {
                      //console.log(currentVideo);
                      video.play().catch((e) => {
                        console.log(e);
                      });

                      short.answers.map((answer) => {
                        answer.is_correct === true &&
                          setCorrectAnswer(answer.id);
                      });
                    } else {
                      video.pause();
                    }
                  }}
                >
                  <div className="container-short">
                    <video src={short.short_url} controls loop></video>
                    <div>
                      <p
                        className={
                          show
                            ? `transcription-short`
                            : "transcription-short hidden"
                        }
                      >
                        Lorem ipsum dolor sit amet consectetur adipisicing elit.
                        Facere error quisquam voluptate voluptatibus nihil
                        consectetur, doloremque dolorum quam iusto odio id
                        voluptas optio excepturi architecto porro facilis quo
                        tempore? Eum!
                      </p>
                      <button onClick={goBack}>back</button>
                      <button onClick={advance}>next</button>
                      <button onClick={handleText}>
                        {show ? "hide trasncription" : "show trasncription"}
                      </button>
                      <div>
                        <h3>{short.question}</h3>
                      </div>
                    </div>
                    <br />
                    <div>
                      <div>
                        {short.answers.map((answer, i) => {
                          return (
                            <label
                              key={answer.id}
                              className="block-radio"
                              htmlFor={answer.id}
                            >
                              <input
                                onChange={handleChangeRadio}
                                type="radio"
                                id={answer.id}
                                name="radio_answet"
                                value={answer.answer_text}
                                //  checked={answerSelected[answer.id] || false}
                              />

                              {answer.answer_text}
                            </label>
                          );
                        })}
                      </div>
                      <button onClick={getValue}>send</button>
                    </div>
                  </div>
                </InView>
              </SwiperSlide>
            );
          })
        )}
      </Swiper>
    </>
  );
};

export default ShortsV2;
