import { useContext } from "react";
import { useEffect } from "react";
import { useState } from "react";
import AuthContext from "../context/AuthContext";
import Loader from "./Loader";

import Modal from "react-modal";

const customStyles = {
  content: {
    // color: "white",

    width: "350px",
    height: "350px",
    backgroundColor: "#00000000",

    outline: "none",
  },
  overlay: { zIndex: 999, backgroundColor: "#18191ab1" },
};
const DeleteCards = () => {
  let { user } = useContext(AuthContext);

  const [cards, setCards] = useState();
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const [actualCardTitle, setActualCardTitle] = useState("");
  const [actualCardImg, setActualCardImg] = useState("");
  const fetchAPi = async () => {
    let url = `http://127.0.0.1:8000/api/usercards/${user.user_id}`;

    const response = await fetch(url);
    const responseJSON = await response.json();
    setCards(responseJSON);
    console.log(responseJSON);
  };
  const deleteCardF = () => {};
  const openModal = () => {
    modalIsOpen ? setModalIsOpen(false) : setModalIsOpen(true);

    // // e.preventDefault();
  };
  const getCurrentData = (title, img) => {
    setActualCardTitle(title);
    setActualCardImg(img);
  };
  useEffect(() => {
    fetchAPi();
  }, []);

  return (
    <>
      <div className="container-cards-delete">
        {!cards ? (
          <Loader></Loader>
        ) : (
          cards.cards.map((card) => {
            return (
              <div key={card.id} className="container-card-delete">
                <h3 style={{ textAlign: "center", margin: "5px" }}>
                  {card.cardTitle}
                </h3>
                <img className="delete-img-card" src={card.imageURL} alt="" />
                <div style={{ textAlign: "center" }}>
                  <button
                    onClick={() => {
                      openModal();
                      getCurrentData(card.cardTitle, card.imageURL);
                    }}
                    className="btn-delete-card"
                  >
                    Eliminar
                  </button>
                </div>
              </div>
            );
          })
        )}
      </div>
      <Modal
        className="modal"
        ariaHideApp={false}
        style={customStyles}
        isOpen={modalIsOpen}
      >
        <div style={{ textAlign: "center" }}>
          <div>
            <h2>
              Â¡Se eliminara la carta:
              <span style={{ backgroundColor: "red", color: "#087FFF" }}>
                {" "}
                {actualCardTitle}
              </span>
              !
            </h2>{" "}
          </div>
          <button className="btn-delete-card">ok</button>
          <button className="btn-cancel-delete" onClick={openModal}>
            Cancelar
          </button>
        </div>
      </Modal>
    </>
  );
};

export default DeleteCards;
