import React, { useEffect, useState } from "react";

import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/effect-cards";
import "./stylesCards.css";

import { EffectCards, Mousewheel } from "swiper";
import wordSound from "../media/cards/audio.png";

import Loader from "./Loader";
import AboutUser from "./AboutUser";
const Cards = () => {
  let url = "http://127.0.0.1:8000/api/cards/";
  const [isActive, setIsActive] = useState(true);

  const [cards, setCards] = useState();

  const fetchAPi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setCards(responseJSON);
    // console.log(responseJSON);
  };

  useEffect(() => {
    fetchAPi();
  }, []);

  const handleDisplay = (e) => {
    isActive ? setIsActive(false) : setIsActive(true);
    // console.log(e.target.textContent);
  };

  var speechSynthesis = require("speech-synthesis");

  var cont = -1;
  var arr = ["#1F1A24", "#172A41", "#1C3940"];
  return (
    <>
      <AboutUser></AboutUser>
      <Swiper
        style={{}}
        mousewheel={true}
        className="mySwiper"
        effect={"cards"}
        modules={[EffectCards, Mousewheel]}
      >
        {!cards ? (
          <Loader></Loader>
        ) : (
          cards.cards.map((card, key) => {
            cont++;
            if (cont === arr.length) {
              cont = 0;
            }
            return (
              <SwiperSlide
                style={{
                  borderWidth: "2px",
                  borderheight: "1px",
                  borderStyle: "solid",

                  backgroundColor: `${arr[cont]}`,
                  borderColor: "white",
                }}
                className="swiper-slide-card"
                key={card.id}
              >
                <div className="container-card">
                  <div className="card">
                    <img className="image-cards" src={card.imageURL} alt="" />
                    <div className="contenido-card">
                      <h3 onClick={handleDisplay} className="card-text">
                        {isActive ? card.cardTitle : card.cardMeaning}
                      </h3>
                      <img
                        onClick={() => {
                          speechSynthesis(card.cardTitle);
                          // console.log(card.cardTitle);
                        }}
                        className="word-sound"
                        src={wordSound}
                        alt=""
                      />
                    </div>
                  </div>
                </div>
              </SwiperSlide>
            );
          })
        )}
      </Swiper>
    </>
  );
};

export default Cards;
