import "bootswatch/dist/darkly/bootstrap.min.css";
import {
  Elements,
  CardElement,
  useStripe,
  useElements,
} from "@stripe/react-stripe-js";

import { loadStripe } from "@stripe/stripe-js";
import axios from "axios";
const stripePromise = loadStripe(
  "pk_test_51KjBqNA9KCn8yVMOEG2TF4LAS9CZwMVfMuAIHu1opMaabVxmgUri9qkETyQ9Q7DGyB6g9bNxEg62zf6dsqQZGdij00t1hmBwwH"
);

const FormCard = () => {
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });
    if (!error) {
      console.log(paymentMethod);

      //   const { id } = paymentMethod;

      //   const { data } = axios.post("http://127.0.0.1:8000/api/checkout/", {
      //     id,
      //     amount: 5000,
      //   });
      //   console.log(data);
    }
  };
  return (
    <>
      <form
        action="http://127.0.0.1:8000/api/checkout/"
        method="POST"
        onSubmit={handleSubmit}
        className="card card-body"
      >
        <div className="form-group">
          <CardElement className="form-control"></CardElement>
        </div>
        <button className="btn btn-success">buy</button>
      </form>
    </>
  );
};

const Stripe = () => {
  //   const options = {
  //     // passing the client secret obtained from the server
  //     clientSecret: "{{CLIENT_SECRET}}",
  //   };
  return (
    <Elements stripe={stripePromise}>
      <div className="payment-container">
        <div class="container p-4">
          <div class="row">
            <div class="col-md-7 offset-md-2">
              <FormCard />
            </div>
          </div>
        </div>
      </div>
    </Elements>
  );
};

export default Stripe;
