import { useState } from "react";
import "./styles/formCard.css";
import { helpHttp } from "../helpers/helpHttp";
import { useContext } from "react";
import AuthContext from "../context/AuthContext";
import ReactFileReader from "react-file-reader";
import Modal from "react-modal";
import crossOut from "../media/crossout.png";

const url = "https://englishapputc.herokuapp.com/api/cards/";

const customStyles = {
  content: {
    // color: "white",

    width: "350px",
    height: "350px",
    backgroundColor: "#00000000",

    outline: "none",
  },
  overlay: { zIndex: 999, backgroundColor: "#18191ab1" },
};
const FormCard = ({ modalIsOpen, openModal, fetchApi }) => {
  const [message, setMessage] = useState(false);
  // const [modalIsOpen, setModalIsOpen] = useState(true);
  let { user } = useContext(AuthContext);

  const initialForm = {
    user: user.user_id,
    title: "",
    meaning: "",
    file: "",
  };
  // const openModal = (e) => {
  //   // e.preventDefault();
  //   modalIsOpen ? setModalIsOpen(false) : setModalIsOpen(true);
  // };

  const [form, setForm] = useState(initialForm);

  const createData = (data) => {
    console.log(form);
    // if (!form.title === "" || !form.meaning === "" || !form.file === "") {
    if (form.title === "" || form.meaning === "" || form.file === "") {
      alert("llena todo, no seas mamon xd");
    } else {
      let options = {
        body: data,
        headers: { "content-type": "multipart/form-data" },
      };
      helpHttp()
        .post(url, options)
        .then((res) => {
          res.err ? setMessage(true) : setMessage(false);
          fetchApi();
        });
    }
    // } else {
    //   alert("llena");
    // }
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(e.taget);
    console.log(form);

    createData(form);
  };
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleFiles = (file) => {
    setForm({
      ...form,
      ["file"]: file.base64,
    });
  };
  return (
    <Modal
      className="modal"
      ariaHideApp={false}
      style={customStyles}
      isOpen={modalIsOpen}
      // contentLabel="Minimal Modal Example"
    >
      <img
        style={{ margin: "10px" }}
        className="cross-out"
        onClick={openModal}
        src={crossOut}
        alt=""
      />
      <div className="container-form-card">
        <div className="form-card">
          <div>
            <h3>¡Sube tus cartas!</h3>
          </div>
          <div>
            <input
              name="title"
              value={form.title}
              onChange={handleChange}
              className="input-form-card"
              type="text"
              placeholder="Palabra en ingles"
            />
          </div>
          <div>
            <input
              name="meaning"
              value={form.meaning}
              onChange={handleChange}
              className="input-form-card"
              type="text"
              placeholder="signicado"
            />
          </div>
          <br />
          <div className="main_div">
            <ReactFileReader handleFiles={handleFiles} base64={true}>
              <button>Selecciona imagen</button>
            </ReactFileReader>
          </div>
          <div> Ó </div>
          <div>
            <input type="text" placeholder="pega la url hacia la imagen" />
          </div>
          <div>
            <button
              onClick={handleSubmit}
              type="submit"
              className="btn-send-card"
            >
              Enviar
            </button>
          </div>
        </div>
      </div>
      {message && (
        <div style={{ textAlign: "center", color: "red" }}>
          ups Algo salio mal, intentalo de nuevo
        </div>
      )}
    </Modal>
  );
};

export default FormCard;
