import { Swiper, SwiperSlide } from "swiper/react";
import { EffectCube, Pagination } from "swiper";
import "swiper/css";
import "swiper/css/effect-cube";
import "swiper/css/pagination";
import video from "../media/video.mp4";
import { useState } from "react";
import ReadMoreAndLess from "react-read-more-less";

const ShortsV2 = () => {
  const [show, setShow] = useState(false);

  const handleText = () => {
    show ? setShow(false) : setShow(true);
  };

  return (
    <>
      <div className="container-short">
        <video src={video} controls></video>
        <div>
          <input type="checkbox" className="read-more-state" id="text" />
          <span className="read-more-target">
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Magnam sunt
            doloremque consequatur quia perferendis illo maxime, commodi esse
            nisi fugiat cum. Tempora blanditiis pariatur possimus aliquam
            consequatur quo incidunt rerum.
          </span>
        </div>
        <ReadMoreAndLess
          ref={this.ReadMore}
          className="read-more-content"
          charLimit={250}
          readMoreText="Read more"
          readLessText="Read less"
        >
          {"jaajajaj"}
        </ReadMoreAndLess>
      </div>
    </>
  );
};

export default ShortsV2;
