import { useState } from "react";
import "./styles/formCard.css";
import { helpHttp } from "../helpers/helpHttp";
import { useContext } from "react";
import AuthContext from "../context/AuthContext";
import ReactFileReader from "react-file-reader";
const url = "http://127.0.0.1:8000/api/cards/";

const FormCard = () => {
  let { user } = useContext(AuthContext);
  console.log(user.user_id);
  const initialForm = {
    user: user.user_id,
    title: "",
    meaning: "",
    file: "",
  };

  const [form, setForm] = useState(initialForm);

  const createData = (data) => {
    let options = {
      body: data,
      headers: { "content-type": "multipart/form-data" },
    };
    helpHttp()
      .post(url, options)
      .then((res) => {
        console.log(res);
      });
    console.log();
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(e.taget);
    console.log(form);

    createData(form);
  };
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleFiles = (file) => {
    setForm({
      ...form,
      ["file"]: file.base64,
    });
  };
  return (
    <div className="container-form-card">
      <form
        autoComplete="off"
        className="form-card"
        action=""
        onSubmit={handleSubmit}
      >
        <div>
          <h3>¡Sube tus cartas!</h3>
        </div>
        <div>
          <input
            name="title"
            value={form.title}
            onChange={handleChange}
            className="input-form-card"
            type="text"
            placeholder="Palabra en ingles"
          />
        </div>
        <div>
          <input
            name="meaning"
            value={form.meaning}
            onChange={handleChange}
            className="input-form-card"
            type="text"
            placeholder="signicado"
          />
        </div>
        <br />
        <div className="container-btn-img">
          <ReactFileReader handleFiles={handleFiles} base64={true}>
            <button className="btn">Selecciona tu imagen aquí</button>
          </ReactFileReader>
        </div>
        <div>
          <button
            // onClick={handleSubmit}
            type="submit"
            className="btn-send-card"
          >
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
};

export default FormCard;
