import example from "../media/a4.jpg";
import { useEffect, useState } from "react";
import Heart from "react-heart";
import hearts from "../media/heart.svg";

let url = "http://127.0.0.1:8000/api/posts/";
const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [heartActive, setHeartActive] = useState(false);

  let stateheart = 1;
  const fetchAPi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setPosts(responseJSON);

    console.log(responseJSON);
  };

  useEffect(() => {
    fetchAPi();
  }, []);

  return (
    <>
      <div class="container-posts">
        <div class="card-post">
          <img
            className="image-post"
            src={example}
            alt="Avatar"
            style={{ width: "100%" }}
          />
          <div className="section-post">consejos</div>
          <div class="container-card-text">
            <h4>
              <b>titulo de post aqui aqui va sis si</b>
            </h4>
          </div>
          <div className="like-post">
            <Heart
              style={{ width: "20px" }}
              isActive={false}
              onClick={() => setHeartActive(!heartActive)}
            />
            <div className="number-likes-post">10 likes</div>
          </div>
        </div>
        <div class="card-post">
          <img
            className="image-post"
            src={example}
            alt="Avatar"
            style={{ width: "100%" }}
          />
          <div className="section-post">consejos</div>
          <div class="container-card-text">
            <h4>
              <b>titulo de post aqui aqui va sis si</b>
            </h4>
          </div>
          <div className="like-post">
            <Heart
              style={{ width: "20px" }}
              isActive={heartActive && stateheart === 2}
              onClick={() => setHeartActive(!heartActive)}
            />
            <div className="number-likes-post">10 likes</div>
          </div>
        </div>
        <div class="card-post">
          <img
            className="image-post"
            src={example}
            alt="Avatar"
            style={{ width: "100%" }}
          />
          <div className="section-post">consejos</div>
          <div class="container-card-text">
            <h4>
              <b>titulo de post aqui aqui va sis si</b>
            </h4>
          </div>
          <div className="like-post">
            <Heart
              style={{ width: "20px" }}
              isActive={heartActive}
              onClick={() => setHeartActive(!heartActive)}
            />
            <div className="number-likes-post">10 likes</div>
          </div>
          <img className="svg-heart" src={hearts} alt="" />
        </div>
      </div>
    </>
  );
};

export default Posts;
