import usuario from "../media/usuario.png";
import Modal from "react-modal";
import { useContext, useState } from "react";
import AuthContext from "../context/AuthContext";
import { helpHttp } from "../helpers/helpHttp";
Modal.defaultStyles.overlay.backgroundColor = "rgba(0, 0, 0, 0.83)";

const customStyles = {
  content: {
    zindex: 10,
    color: "white",
    width: "280px",
    height: "400px",
    backgroundColor: "#16181C",
  },
};

const AboutUser = () => {
  let { user, logoutUser } = useContext(AuthContext);

  const [isPremium, setIsPremium] = useState(false);
  let url = `http://127.0.0.1:8000/api/users/${user.user_id}`;

  helpHttp()
    .get(url)
    .then((res) => {
      //  console.log(res.user.premium);
      if (res.user.premium == true) {
        setIsPremium("Activo");
      } else {
        setIsPremium("Inactivo");
      }
      if (!res.err) {
        return;
        //  console.log("no error getting userlogged info");
      } else {
        return;
        // console.log(res);
      }
    });

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const openModal = () => {
    modalIsOpen ? setModalIsOpen(false) : setModalIsOpen(true);
  };
  return (
    <div className="userinfo-container">
      {user ? <button onClick={logoutUser}>logout</button> : <span>login</span>}
      <div className="avatar">
        <img onClick={openModal} src={usuario} alt="" />
        <Modal
          ariaHideApp={false}
          style={customStyles}
          isOpen={modalIsOpen}
          contentLabel="Minimal Modal Example"
        >
          <h2 className="textito">Bienvenido {user.username}</h2>
          <h2> premium status: {isPremium} </h2>
        </Modal>
      </div>
    </div>
  );
};

export default AboutUser;
