import "./styles/formCard.css";

const initialForm = {
  title: "",
  meaning: "",
  file: "",
};
const FormCard = () => {
  const [form, setForm] = useState(initialForm);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(e.taget);
  };
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };
  return (
    <div className="container-form-card">
      <form className="form-card" action="" onSubmit={handleSubmit}>
        <div>
          <h3>Â¡Sube tus cartas!</h3>
        </div>
        <div>
          <input
            onChange={handleChange}
            className="input-form-card"
            type="text"
            placeholder="Palabra en ingles"
          />
        </div>
        <div>
          <input
            onChange={handleChange}
            className="input-form-card"
            type="text"
            placeholder="signicado"
          />
        </div>
        <br />
        <div className="container-btn-img">
          <label className="label-file" htmlFor="file-input">
            Selecciona tu imagen
          </label>
          <br />
          <input
            onChange={handleChange}
            // size="60"
            id="file-input"
            className="file-input"
            type="file"
            placeholder="jaja"
          />
        </div>
        <div>
          <button
            // onClick={handleSubmit}
            type="submit"
            className="btn-send-card"
          >
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
};

export default FormCard;
