import { useState } from "react";
import "./styles/formCard.css";
import { helpHttp } from "../helpers/helpHttp";
import { useContext } from "react";
import AuthContext from "../context/AuthContext";
import ReactFileReader from "react-file-reader";
import Modal from "react-modal";
import crossOut from "../media/crossout.png";

const url = "http://127.0.0.1:8000/api/cards/";

const customStyles = {
  content: {
    // color: "white",
    width: "350px",
    height: "150px",
    backgroundColor: "#00000000",

    // border: "none",
  },
  overlay: { zIndex: 999, backgroundColor: "#00000000" },
};
const FormCard = () => {
  const [modalIsOpen, setModalIsOpen] = useState(true);
  let { user } = useContext(AuthContext);
  console.log(user.user_id);
  const initialForm = {
    user: user.user_id,
    title: "",
    meaning: "",
    file: "",
  };
  const openModal = (e) => {
    // e.preventDefault();
    modalIsOpen ? setModalIsOpen(false) : setModalIsOpen(true);
  };

  const [form, setForm] = useState(initialForm);

  const createData = (data) => {
    let options = {
      body: data,
      headers: { "content-type": "multipart/form-data" },
    };
    helpHttp()
      .post(url, options)
      .then((res) => {
        console.log(res);
      });
    console.log();
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(e.taget);
    console.log(form);

    createData(form);
  };
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleFiles = (file) => {
    setForm({
      ...form,
      ["file"]: file.base64,
    });
  };
  return (
    <div className="container-modal-card">
      <Modal
        ariaHideApp={false}
        style={customStyles}
        isOpen={modalIsOpen}
        contentLabel="Minimal Modal Example"
      >
        <img
          style={{ margin: "10px" }}
          className="cross-out"
          onClick={openModal}
          src={crossOut}
          alt=""
        />
        <div className="container-form-card">
          <form
            autoComplete="off"
            className="form-card"
            action=""
            onSubmit={handleSubmit}
          >
            <div>
              <h3>¡Sube tus cartas!</h3>
            </div>
            <div>
              <input
                name="title"
                value={form.title}
                onChange={handleChange}
                className="input-form-card"
                type="text"
                placeholder="Palabra en ingles"
              />
            </div>
            <div>
              <input
                name="meaning"
                value={form.meaning}
                onChange={handleChange}
                className="input-form-card"
                type="text"
                placeholder="signicado"
              />
            </div>
            <br />
            <div className="container-btn-img">
              <ReactFileReader handleFiles={handleFiles} base64={true}>
                <button className="btn">Selecciona tu imagen aquí</button>
              </ReactFileReader>
            </div>
            <div>
              <button
                // onClick={handleSubmit}
                type="submit"
                className="btn-send-card"
              >
                Enviar
              </button>
            </div>
          </form>
        </div>
      </Modal>
    </div>
  );
};

export default FormCard;
