import { useState } from "react";
import "./styles/formCard.css";
import { helpHttp } from "../helpers/helpHttp";

const initialForm = {
  title: "",
  meaning: "",
  file: "",
};

const FormCard = () => {
  const [form, setForm] = useState(initialForm);

  const createData = (data) => {
    let options = {
      body: data,
      headers: { "content-type": "application/json" },
    };
    helpHttp()
      .post(options)
      .then((res) => {
        console.log(res);
      });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(e.taget);
    // console.log(form);
    createData(form);
  };
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="container-form-card">
      <form
        autoComplete="off"
        className="form-card"
        action=""
        onSubmit={handleSubmit}
      >
        <div>
          <h3>Â¡Sube tus cartas!</h3>
        </div>
        <div>
          <input
            name="title"
            value={form.title}
            onChange={handleChange}
            className="input-form-card"
            type="text"
            placeholder="Palabra en ingles"
          />
        </div>
        <div>
          <input
            name="meaning"
            value={form.meaning}
            onChange={handleChange}
            className="input-form-card"
            type="text"
            placeholder="signicado"
          />
        </div>
        <br />
        <div className="container-btn-img">
          <label className="label-file" htmlFor="file-input">
            Selecciona tu imagen
          </label>
          <br />
          <input
            accept="image/*"
            onChange={handleChange}
            name="file"
            value={form.file}
            // size="60"
            id="file-input"
            className="file-input"
            type="file"
            placeholder="jaja"
          />
        </div>
        <div>
          <button
            // onClick={handleSubmit}
            type="submit"
            className="btn-send-card"
          >
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
};

export default FormCard;
