import { InView } from "react-intersection-observer";
import { Swiper, SwiperSlide } from "swiper/react";
import { Mousewheel, Pagination } from "swiper";
import "swiper/css";
import "swiper/css/pagination";
import left from "../media/left-arrow.png";
import right from "../media/right-arrow.png";

import { useEffect, useState } from "react";
import Loader from "./Loader";

let url = "http://127.0.0.1:8000/api/shortsetV2/";
const ShortsV2 = () => {
  const [show, setShow] = useState(false);
  const [correctAnswer, setCorrectAnswer] = useState("");
  const [answerSelected, setAnswerSelected] = useState();
  const [currentVideo, setCurrentVideo] = useState();
  const [shorts, setShorts] = useState([]);
  const [videoIsEnd, setVideoIsEnd] = useState(false);
  const [btnTranscription, setBtnTranscription] = useState(false);
  const fetchAPi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setShorts(responseJSON);

    console.log(responseJSON);
  };
  useEffect(() => {
    fetchAPi();
    document.addEventListener("keydown", clicked, true);
  }, []);
  const clicked = (e) => {
    if (e.key === " ") {
      // console.log(currentVideo);
      // currentVideo && currentVideo.pause();
    }
  };

  useEffect(() => {
    console.log(correctAnswer);
  }, [correctAnswer]);

  const handleText = () => {
    show ? setShow(false) : setShow(true);
  };
  const handleChangeRadio = (e) => {
    setAnswerSelected(e.target.value);
  };

  const getValue = () => {
    console.log(answerSelected, correctAnswer);

    answerSelected == correctAnswer ? alert("correct") : alert("fail");
  };

  const advance = () => {
    // const video = document.querySelector("video");
    currentVideo.currentTime = currentVideo.currentTime + 2;
  };
  const goBack = () => {
    ////   const video = document.querySelector("video");
    currentVideo.currentTime = currentVideo.currentTime - 2;
  };
  const PlayBtn = () => {
    currentVideo.paused ? currentVideo.play() : currentVideo.pause();
  };

  return (
    <>
      <Swiper
        mousewheel={true}
        style={{ height: "100vh" }}
        className="swiper-shorts"
        direction={"vertical"}
        // pagination={{ clickable: true }}
        slidesPerView={1}
        spaceBetween={30}
        modules={[Pagination, Mousewheel]}
      >
        {!shorts ? (
          <Loader></Loader>
        ) : (
          shorts.map((short) => {
            return (
              <SwiperSlide key={short.id}>
                <InView
                  os="div"
                  onChange={(inView, entry) => {
                    var video = entry.target.querySelector("video");

                    if (inView) {
                      setCurrentVideo(video);
                      //console.log(currentVideo);
                      video.play().catch((e) => {
                        console.log(e);
                      });

                      short.answers.map((answer) => {
                        answer.is_correct === true &&
                          setCorrectAnswer(answer.id);
                      });

                      video.onended = function (e) {
                        console.log("termino");
                        setVideoIsEnd(true);
                        console.log(e);
                        setBtnTranscription(true); //
                      };
                    } else {
                      video.pause();
                    }
                  }}
                >
                  <div className="container-short">
                    <video
                      onClick={PlayBtn}
                      src={short.short_url}
                      controls
                      // loop
                    ></video>
                    <div>
                      <p
                        className={
                          show
                            ? `transcription-short`
                            : "transcription-short hidden"
                        }
                      >
                        {short.translation}
                      </p>

                      <button
                        disabled={btnTranscription ? true : false}
                        onClick={handleText}
                        className="btn-transcription"
                      >
                        {show ? "hide trasncription" : "show trasncription"}
                      </button>
                      <div>
                        <button className="btn-control-video" onClick={goBack}>
                          <img style={{ width: "30px" }} src={left} alt="" />
                        </button>
                        <button className="btn-control-video" onClick={advance}>
                          <img src={right} alt="" />
                        </button>
                      </div>
                      <div
                        className={
                          videoIsEnd
                            ? "container-question"
                            : "container-question hidden"
                        }
                      >
                        <h3>{short.question}</h3>
                      </div>
                    </div>
                    <br />
                    <div>
                      <div>
                        {short.answers.map((answer, i) => {
                          return (
                            <label
                              key={answer.id}
                              className="block-radio"
                              htmlFor={answer.id}
                            >
                              <input
                                className="input-radio"
                                onChange={handleChangeRadio}
                                type="radio"
                                id={answer.id}
                                name="radio_answet"
                                value={answer.id}
                                //  checked={answerSelected[answer.id] || false}
                              />
                              {answer.answer_text}
                            </label>
                          );
                        })}
                      </div>
                      <div className="container-btn-send-answer">
                        <button className="send-answer" onClick={getValue}>
                          send
                        </button>
                      </div>
                    </div>
                  </div>
                </InView>
              </SwiperSlide>
            );
          })
        )}
      </Swiper>
    </>
  );
};

export default ShortsV2;
