import { useEffect, useState } from "react";

import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/effect-cards";
import "./stylesCards.css";

import { EffectCards, Mousewheel, EffectCube } from "swiper";
import wordSound from "../media/cards/audio.png";

import Loader from "./Loader";
import AboutUser from "./AboutUser";
const Cards = () => {
  let url = "http://127.0.0.1:8000/api/cards/";
  const [isActive, setIsActive] = useState(true);

  const [cards, setCards] = useState();
  const params = {
    method: "GET",
    headers: {
      accept: "application/json",
    },
  };
  const fetchAPi = async () => {
    const response = await fetch(url, params);
    const responseJSON = await response.json();
    setCards(responseJSON);
    // console.log(responseJSON);
  };

  useEffect(() => {
    fetchAPi();
  }, []);

  const handleDisplay = (e) => {
    isActive ? setIsActive(false) : setIsActive(true);
    // console.log(e.target.textContent);
  };

  var speechSynthesis = require("speech-synthesis");

  var cont = -1;
  var arr = ["#1F1A24", "#282A36"];
  return (
    <>
      <Swiper
        effect={"cube"}
        grabCursor={true}
        cubeEffect={{
          shadow: true,
          slideShadows: true,
          shadowOffset: 20,
          shadowScale: 0.94,
        }}
        pagination={true}
        modules={[EffectCube, Pagination]}
        className="mySwiper"
      >
        <SwiperSlide>
          <img src="https://swiperjs.com/demos/images/nature-1.jpg" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="https://swiperjs.com/demos/images/nature-2.jpg" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="https://swiperjs.com/demos/images/nature-3.jpg" />
        </SwiperSlide>
        <SwiperSlide>
          <img src="https://swiperjs.com/demos/images/nature-4.jpg" />
        </SwiperSlide>
      </Swiper>
    </>
  );
};

export default Cards;
