import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Loader from "./Loader";

const parametros = {
  method: "GET",
  headers: {
    accept: "application/json",
  },
};

const PostPage = () => {
  const [post, setPost] = useState();
  const params = useParams();

  const fetchAPi = async () => {
    let url = `http://127.0.0.1:8000/api/post/${params.id}`;
    const response = await fetch(url, parametros);
    const responseJSON = await response.json();
    setPost(responseJSON.post[0]);
  };

  useEffect(() => {
    fetchAPi();
  }, []);

  return (
    <>
      <div className="container-post-content">
        {!post ? (
          <Loader></Loader>
        ) : (
          <div className="container-post-content">
            <h4>{post.title}</h4>
            <div className="container-post-content-img">
              <img src={post.image_src} alt="" />
            </div>
            <div className="box-text-post">{post.content}</div>

            <section>
              <div className="comment-section">
                <button>jaja</button>
              </div>
            </section>
          </div>
        )}
        {console.log(post)}
        {/* {post && console.log(post.post[0].title)} */}
      </div>
    </>
  );
};

export default PostPage;
