import { useEffect, useState } from "react";
import Loader from "./Loader";
import { InView } from "react-intersection-observer";

import heart from "../media/heart.png";
import "swiper/css";

import { Pagination, Mousewheel } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import Yair from "./Yair";

const Shorts = () => {
  const [shorts, setShorts] = useState();

  const [isplay, setIsplay] = useState(true);

  const fetchAPi = async () => {
    const urlShorts = "http://127.0.0.1:8000/api/shortset/";
    const params = {
      method: "GET",
      headers: {
        accept: "application/json",
      },
    };
    const response = await fetch(urlShorts, params);
    const responseJSON = await response.json();
    console.log(responseJSON);
    setShorts(responseJSON);
  };

  useEffect(() => {
    fetchAPi();
  }, []);

  const handlePlay = (e) => {
    let video = e.target;
    if (isplay) {
      setIsplay(false);

      video.pause();
    } else {
      setIsplay(true);

      video.play();
    }
    //  video.currentTime = 19;
  };

  // const swiperSlide = useSwiperSlide();

  const [currentVideo, setCurrentVideo] = useState();
  return (
    <>
      <Swiper
        mousewheel={true}
        className="swiper-shorts"
        direction={"vertical"}
        pagination={{
          clickable: true,
        }}
        modules={[Pagination, Mousewheel]}
        spaceBetween={100}
        slidesPerView={1}
      >
        {!shorts ? (
          <Loader></Loader>
        ) : (
          shorts.map((short, key) => {
            return (
              <SwiperSlide key={key}>
                <InView
                  as="div"
                  onChange={(inView, entry) => {
                    // console.log(inView);
                    var video = entry.target.querySelector("video");

                    if (inView) {
                      setCurrentVideo(video);

                      video.play().catch((e) => {
                        console.log(e);
                      });
                    } else {
                      video.pause();
                    }

                    //   handlePlay;
                  }}
                >
                  <div className="shorts-container">
                    <div className="short-video">
                      <video
                        // inView={inView}
                        //  muted={false}
                        onClick={handlePlay}
                        loop
                        src={short.short_url}
                      ></video>
                    </div>
                  </div>
                </InView>

                <Swiper
                  data-id={key}
                  modules={[Pagination]}
                  spaceBetween={10}
                  slidesPerView={1}
                >
                  {/*in every swiperslide data-swiper-autoplay="2000"*/}

                  {short.sub_card_list.map((subCard, index) => {
                    // console.log(subCard);
                    return (
                      <SwiperSlide
                        className="swiper-slide-subtitles"
                        key={subCard.id}
                      >
                        <InView
                          onChange={(inView, entry2) => {
                            // console.log(video);
                            {
                              console.log();
                            }
                            if (inView) {
                              let video =
                                entry2.target.parentElement.parentElement.parentElement.parentElement.querySelector(
                                  "video"
                                );

                              let specificSecond = subCard.skip_to;

                              video.currentTime = specificSecond;
                            }
                          }}
                        >
                          <div className="short-info">
                            <p className="sentence-short">{subCard.sentence}</p>
                            <p className="sentence-meaning-short">
                              {subCard.meaning}
                            </p>
                          </div>
                        </InView>

                        <Yair video={currentVideo}></Yair>
                      </SwiperSlide>
                    );
                  })}
                </Swiper>
                <div className="container-likes-short">
                  <button className="btn-like-short">
                    <img src={heart} alt="likes" />
                    <div className="num-likes-short">{short.likes}</div>
                  </button>
                </div>
              </SwiperSlide>
            );
          })
        )}
      </Swiper>
    </>
  );
};

export default Shorts;
