import React, { useContext, useEffect, useState } from "react";
import "./styles/cardStyles.css";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/effect-cards";
import "./styles/stylesCards.css";
import { Pagination, EffectCards, Mousewheel, Keyboard } from "swiper";
import wordSound from "../media/cards/audio.png";
import iconAdd from "../media/add.png";
import Loader from "./Loader";
import AboutUser from "./AboutUser";
import CardTuto from "./CardTuto";
import MenuBar from "./MenuBar";
import { Link, NavLink, useParams } from "react-router-dom";
import AuthContext from "../context/AuthContext";
import FormCard from "./FormCard";
import PayPal from "./PayPal";
import { helpHttp } from "../helpers/helpHttp";
import { RadioGroup, RadioButton } from "react-radio-buttons";
let url = "";
const urlImageCard = "https://res.cloudinary.com/tolumaster/image/upload/v1/";
const Cards = () => {
  let { user } = useContext(AuthContext);
  const paramsUrl = useParams();
  const [isActive, setIsActive] = useState(true);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [isPremium, setIsPremium] = useState(false);
  const [cardLength, setCardLength] = useState(0);
  const [cards, setCards] = useState([]);
  const [answerSelected, setAnswerSelected] = useState();

  const [cardPicked, setCardPicked] = useState();
  const [answerOnes, setAnswerOnes] = useState();
  const [answerTwos, setAnswerTwos] = useState();
  const [answers, setAnswers] = useState([]);
  const params = {
    method: "GET",
    headers: {
      accept: "application/json",
    },
  };

  const fetchAPi = async () => {
    // console.log("fetched");
    paramsUrl.section === "mis-cartas"
      ? (url = `https://englishapputc.herokuapp.com/api/usercards/${user.user_id}`)
      : (url = `https://englishapputc.herokuapp.com/api/cards/${paramsUrl.section}`);
    const response = await fetch(url, params);
    const responseJSON = await response.json();
    setCards(responseJSON);
    setCardLength(responseJSON.cards.length);
    // console.log(responseJSON.cards.length);
  };

  const getUserData = () => {
    helpHttp()
      .get(`https://englishapputc.herokuapp.com/api/users/${user.user_id}`)
      .then((res) => {
        // console.log(res.user.premium);
        setIsPremium(res.user.premium);
      });
  };

  useEffect(() => {
    fetchAPi();
    getUserData();
    // console.log(setCards);
  }, []);

  const handleDisplay = (e) => {
    isActive ? setIsActive(false) : setIsActive(true);
  };

  var speechSynthesis = require("speech-synthesis");
  const openModal = (e) => {
    // e.preventDefault();
    modalIsOpen ? setModalIsOpen(false) : setModalIsOpen(true);
  };

  function differentRandom() {
    var randoms = [];
    while (randoms.length < 3) {
      var r = Math.floor(Math.random() * cardLength - 1) + 1;
      if (randoms.indexOf(r) === -1) randoms.push(r);
    }
    console.log(randoms);
    return randoms;
  }
  const generateQuestion = () => {
    const lista = [];

    const cartas = cards.cards;
    const randomsGenerated = differentRandom();
    const cardChoosen = cartas[randomsGenerated[0]];
    lista.push(cardChoosen);
    setCardPicked(cardChoosen);

    const answerOne = cartas[randomsGenerated[1]];
    lista.push(answerOne);

    const answerTwo = cartas[randomsGenerated[2]];
    lista.push(answerTwo);

    setAnswers(lista.sort(() => Math.random() - 0.5));
    console.log(answers);
  };

  const handleChangeRadio = (e) => {
    setAnswerSelected(e.target.value);
  };
  const handleAnswer = () => {
    if (answerSelected === cardPicked.cardMeaning) {
      console.log("correct");
    } else {
      console.log("incorrect");
    }
  };

  return (
    <>
      <AboutUser></AboutUser>

      <div classNameName="all-cards">
        {paramsUrl.section === "mis-cartas" && (
          <div classNameName="del-cards">
            <NavLink to="/delete">
              <button classNameName="btn-delete-cards">Eliminar cartas</button>
            </NavLink>
          </div>
        )}

        <Swiper
          keyboard={true}
          mousewheel={true}
          classNameName="mySwiper"
          effect={"cards"}
          modules={[EffectCards, Mousewheel, Pagination, Keyboard]}
        >
          {/* <CardTuto></CardTuto> */}
          {!cards ? (
            <Loader></Loader>
          ) : !cards.cards ? (
            <div classNameName="nada-por-aqui">Nada por aqui...</div>
          ) : (
            cards.cards.map((card, key) => {
              // console.log(cards);
              return (
                <SwiperSlide
                  style={{
                    borderColor: "white",
                    backgroundImage:
                      "url(" +
                      (card.imageURL === ""
                        ? urlImageCard + card.cardImage
                        : card.imageURL) +
                      ")",
                  }}
                  classNameName="swiper-slide-card"
                  key={card.id}
                >
                  <div classNameName="container-card">
                    <div classNameName="card">
                      {/* <img classNameName="image-cards" src={card.imageURL} alt="" /> */}
                      <div classNameName="contenido-card">
                        <h3 onClick={handleDisplay} classNameName="card-text">
                          {isActive ? card.cardTitle : card.cardMeaning}
                        </h3>
                        <button
                          classNameName="btn-sound-card"
                          onClick={() => {
                            speechSynthesis(card.cardTitle);
                            // console.log(card.cardTitle);
                          }}
                        >
                          <img
                            classNameName="word-sound"
                            src={wordSound}
                            alt=""
                          />
                        </button>
                        <div>
                          {/* <img src={urlImageCard + card.cardImage} alt="" /> */}
                        </div>
                      </div>
                    </div>
                  </div>
                </SwiperSlide>
              );
            })
          )}
        </Swiper>
        {paramsUrl.section === "mis-cartas" && (
          <div classNameName="container-icon-add">
            {cards.cards && cards.cards.length >= 10 && !isPremium ? (
              <>
                <div classNameName="container-updatein-card">
                  <div classNameName="alert-no-more-cards">
                    <strong>
                      Para seguir agregando cartas, actualízate a premium
                    </strong>
                  </div>
                  <div>
                    <PayPal></PayPal>
                  </div>
                </div>
              </>
            ) : (
              <img
                onClick={openModal}
                classNameName="icon-add"
                src={iconAdd}
                alt=""
              />
            )}
          </div>
        )}
        {cardPicked && (
          <>
            <div>
              Elige el significado de<strong> {cardPicked.cardTitle}</strong>
            </div>
            <div>
              {answers &&
                answers.map((answer, key) => {
                  return (
                    <div key={answer.id} classNameName={"box-answers" && ""}>
                      <input
                        classNameName="input-radio"
                        type="radio"
                        id={answer.id}
                        name="radio_answer"
                      />
                      <label
                        classNameName="block-radio"
                        htmlFor={answer.id}
                      ></label>

                      <label className="rad-label">
                        <input
                          type="radio"
                          className="rad-input"
                          name="rad"
                          onChange={handleChangeRadio}
                          value={answer.cardMeaning}
                        />
                        <div className="rad-design"></div>
                        <div className="rad-text"> {answer.cardMeaning}</div>
                      </label>
                    </div>
                  );
                })}
            </div>
            <div>
              <button onClick={handleAnswer}>send</button>
            </div>
          </>
        )}
        <button onClick={generateQuestion}>kjaaj</button>
      </div>
      <FormCard
        fetchApi={fetchAPi}
        modalIsOpen={modalIsOpen}
        openModal={openModal}
      ></FormCard>

      <MenuBar></MenuBar>
    </>
  );
};

export default Cards;
