import usuario from "../media/usuario.png";
import Modal from "react-modal";
import { useContext, useState } from "react";
import AuthContext from "../context/AuthContext";
Modal.defaultStyles.overlay.backgroundColor = "rgba(0, 0, 0, 0.83)";

const customStyles = {
  content: {
    zindex: "99999",
    color: "white",
    width: "280px",
    height: "400px",
    backgroundColor: "#16181C",
    animation: "linear",
  },
};

const AboutUser = () => {
  let { user, logoutUser } = useContext(AuthContext);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const openModal = () => {
    modalIsOpen ? setModalIsOpen(false) : setModalIsOpen(true);
  };
  return (
    <div className="userinfo-container">
      {user ? <button onClick={logoutUser}>logout</button> : <span>login</span>}
      <div className="avatar">
        <img onClick={openModal} src={usuario} alt="" />
        <Modal
          style={customStyles}
          isOpen={modalIsOpen}
          contentLabel="Minimal Modal Example"
        >
          <h2 className="textito">Bienvenido {user.username}</h2>
        </Modal>
      </div>
    </div>
  );
};

export default AboutUser;
